client.on('message', message => {
	// Ignore messages that aren't from a guild
	if (!message.guild) return;
  
	// If the message content starts with "!kick"
	if (message.content.startsWith(`${prefix}kick`)) {
	  // Assuming we mention someone in the message, this will return the user
	  // Read more about mentions over at https://discord.js.org/#/docs/main/master/class/MessageMentions
	  const user = message.mentions.users.first();
	  // If we have a user mentioned
	  if (user) {
		// Now we get the member from the user
		const member = message.guild.member(user);
		// If the member is in the guild
		if (member) {
		  /**
		   * Kick the member
		   * Make sure you run this on a member, not a user!
		   * There are big differences between a user and a member
		   */
		  member
			.kick('Optional reason that will display in the audit logs')
			.then(() => {
			  // We let the message author know we were able to kick the person
			  message.reply(`Nous avons expusl√© : ${user.tag} avec succ√®s !`);
			})
			.catch(err => {
			  // An error happened
			  // This is generally due to the bot not being able to kick the member,
			  // either due to missing permissions or role hierarchy
			  message.reply("J'ai √©t√© incapable d'expluser ce membre.");
			  // Log the error
			  console.error(err);
			});
		} else {
		  // The mentioned user isn't in this guild
		  message.reply("Cette personne n'est pas dans ce serveur !");
		}
		// Otherwise, if no user was mentioned
	  } else {
		message.reply("Tu n'as pas mentionn√© la personne √† expulser !");
	  }
	}
  });

client.on('message', message => {

	const args = message.content.trim().split(/ +/g);

	if(message.content.startsWith(`${prefix}unban`)) {
		if(!message.member.hasPermission("BAN_MEMBERS")) {
		  return message.channel.send(`**${message.author.username}**, You do not have perms to unban someone`)
		}
		
		if(!message.guild.me.hasPermission("BAN_MEMBERS")) {
		  return message.channel.send(`**${message.author.username}**, I do not have perms to unban someone`)
		}
		
		let userID = args[0]
		  message.guild.fetchBans().then(bans=> {
		  if(bans.size == 0) return 
		  let bUser = bans.find(b => b.user.id == userID)
		  if(!bUser) return
		  message.guild.members.unban(bUser.user)
	})
		
	  ;}
})

